DROP TABLE IF EXISTS `store_book_info`;
CREATE TABLE `store_book_info` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id，主键id',
  `file_name` varchar(256) DEFAULT NULL COMMENT '文件名称',
  `cover` varchar(256) DEFAULT NULL COMMENT '图书封面图片路径',
  `title` varchar(256) DEFAULT NULL COMMENT '标题',
  `author` varchar(256) DEFAULT NULL COMMENT '作者',
  `publisher` varchar(128) DEFAULT NULL COMMENT '出版社',
  `book_id` varchar(256) DEFAULT NULL COMMENT '图书id',
  `category` int(3) DEFAULT NULL COMMENT '分类id',
  `category_text` varchar(256) DEFAULT NULL COMMENT '分类id对应的分类名称',
  `language` varchar(10) DEFAULT NULL COMMENT '语种，cn-中文，en-英文；默认：“cn-中文”',
  `root_file` varchar(256) DEFAULT NULL COMMENT '电子书opf文件路径',
  `selected` int(3) DEFAULT 0 COMMENT '是否被选中（书架中运用），0-未选中，1-已选中；默认：“0-未选中',
  `private` int(3) DEFAULT 0 COMMENT '是否开启私密阅读，0-否，1-是；默认：“0-未开启”',
  `cache` int(3) DEFAULT 0 COMMENT '是否开启离线缓存（书架中会应用到，使用IndexedDB数据库缓存电子书），0-未缓存，1-已缓存；默认“0-未缓存”',
  `have_read` int(3) DEFAULT 0 COMMENT '是否已经阅读过，0-未阅读，1-已阅读；默认：“0-未阅读”',
  `readers` int(11) DEFAULT 0 COMMENT '阅读人数；默认“0-阅读人数为零”',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `idx_file_name` (`file_name`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8mb4;

